import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { CompatClient, Stomp } from '@stomp/stompjs';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Chat = () => {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const stompClient = useRef(null);

  useEffect(() => {
    // Fetch user list
    axios.get('/api/users').then(res => setUsers(res.data.filter(u => u.id !== user.id)));
  }, [user]);

  useEffect(() => {
    // Connect to WebSocket
    const socket = new SockJS('/ws');
    stompClient.current = Stomp.over(socket);
    stompClient.current.connect({}, () => {
      stompClient.current.subscribe(`/user/${user.username}/queue/messages`, (msg) => {
        const message = JSON.parse(msg.body);
        if (selectedUser && message.sender.id === selectedUser.id) {
          setMessages(prev => [...prev, message]);
        }
      });
    });
    return () => stompClient.current && stompClient.current.disconnect();
  }, [user, selectedUser]);

  useEffect(() => {
    // Fetch message history when user selected
    if (selectedUser) {
      axios.get(`/api/chat/${selectedUser.id}`).then(res => setMessages(res.data));
    }
  }, [selectedUser]);

  const sendMessage = () => {
    if (input.trim() && selectedUser) {
      const msg = {
        content: input,
        sender: user,
        receiver: selectedUser,
        type: 'TEXT',
      };
      stompClient.current.send('/app/chat.send', {}, JSON.stringify(msg));
      setMessages(prev => [...prev, { ...msg, sender: user }]);
      setInput('');
    }
  };

  return (
    <div style={{ display: 'flex', height: 400, border: '1px solid #ccc' }}>
      <div style={{ width: 200, borderRight: '1px solid #ccc', overflowY: 'auto' }}>
        <h4>Users</h4>
        <ul>
          {users.map(u => (
            <li key={u.id} style={{ cursor: 'pointer', fontWeight: selectedUser?.id === u.id ? 'bold' : 'normal' }} onClick={() => setSelectedUser(u)}>
              {u.fullName || u.username}
            </li>
          ))}
        </ul>
      </div>
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <div style={{ flex: 1, overflowY: 'auto', padding: 10 }}>
          {messages.map((m, i) => (
            <div key={i} style={{ textAlign: m.sender.id === user.id ? 'right' : 'left' }}>
              <b>{m.sender.fullName || m.sender.username}:</b> {m.content}
            </div>
          ))}
        </div>
        <div style={{ display: 'flex', borderTop: '1px solid #ccc' }}>
          <input value={input} onChange={e => setInput(e.target.value)} style={{ flex: 1 }} onKeyDown={e => e.key === 'Enter' && sendMessage()} />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
