import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { CompatClient, Stomp } from '@stomp/stompjs';
import api from '../services/api';
import { useAuth } from '../contexts/AuthContext';

const WS_URL = '/ws-chat';
const API_USERS = '/api/chat/users';



const Chat = ({ onClose }) => {
	const { user } = useAuth();
	const [users, setUsers] = useState([]);
	const [selectedUser, setSelectedUser] = useState(null);
	const selectedUserRef = useRef(null);
	const [messages, setMessages] = useState([]);
	const [input, setInput] = useState('');
	const [stompClient, setStompClient] = useState(null);
	const [isConnected, setIsConnected] = useState(false);
	const messagesEndRef = useRef(null);

	// Fetch user list
	useEffect(() => {
		api.get(API_USERS).then(res => {
			setUsers(res.data.filter(u => u.id !== user.id));
		});
	}, [user.id]);


	// Keep selectedUserRef in sync with selectedUser
	useEffect(() => {
		selectedUserRef.current = selectedUser;
	}, [selectedUser]);

	// Connect to WebSocket only once on mount
	useEffect(() => {
		const socket = new SockJS(WS_URL);
		const client = Stomp.over(socket);
		setIsConnected(false);
		client.connect({}, () => {
			setIsConnected(true);
			client.subscribe(`/user/${user.username}/queue/messages`, (msg) => {
				const body = JSON.parse(msg.body);
				// Only add message if it is between the logged-in user and the selected user (using ref)
				setMessages(prev => {
					const selUser = selectedUserRef.current;
					if (!selUser) return prev;
					const isBetween =
						(body.sender.id === user.id && body.receiver.id === selUser.id) ||
						(body.sender.id === selUser.id && body.receiver.id === user.id);
					if (isBetween) {
						return [...prev, body];
					}
					return prev;
				});
			});
		}, (err) => {
			setIsConnected(false);
			console.error('STOMP connection error:', err);
		});
		setStompClient(client);
		return () => {
			client.disconnect();
			setIsConnected(false);
		};
		// eslint-disable-next-line
	}, [user.username]);

	// Fetch chat history when user selected
	useEffect(() => {
		if (selectedUser) {
			api.get(`/api/chat/history?userId=${selectedUser.id}`)
				.then(res => {
					setMessages(res.data);
				})
				.catch(err => {
					setMessages([]);
					// Show backend error in alert for easier debugging
					const backendMsg = err?.response?.data?.message || err?.response?.data || err.message;
					console.error('Failed to fetch chat history:', backendMsg);
					alert('Failed to load chat history. Backend says: ' + backendMsg);
				});
		}
	}, [selectedUser]);

	// Scroll to bottom on new message
	useEffect(() => {
		messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
	}, [messages]);

	const sendMessage = (e) => {
		e.preventDefault();
		if (!input.trim() || !selectedUser || !isConnected || !stompClient) return;
		try {
			const msg = {
				content: input,
				receiver: { id: selectedUser.id },
				type: 'TEXT',
				sender: { id: user.id }, // for optimistic UI
			};
			// Optimistically add the message to the chat
			setMessages(prev => [...prev, { ...msg, sender: { id: user.id }, receiver: { id: selectedUser.id } }]);
			stompClient.send('/app/chat.send', {}, JSON.stringify(msg));
			setInput('');
		} catch (err) {
			alert('Unable to send message: STOMP connection not ready.');
			console.error('Send message error:', err);
		}
	};

	return (
		<div style={{ display: 'flex', height: '100%' }}>
			{/* User List Sidebar */}
			<div style={{ width: 120, background: '#f5f5f5', borderRight: '1px solid #eee', overflowY: 'auto' }}>
				<div style={{ padding: 8, fontWeight: 'bold', borderBottom: '1px solid #ddd' }}>Users</div>
				{users.map(u => (
					<div
						key={u.id}
						style={{
							padding: 8,
							cursor: 'pointer',
							background: selectedUser?.id === u.id ? '#e3f2fd' : 'transparent',
							borderBottom: '1px solid #eee',
							fontWeight: selectedUser?.id === u.id ? 'bold' : 'normal',
						}}
						onClick={() => setSelectedUser(u)}
					>
						{u.fullName || u.username}
					</div>
				))}
			</div>
			{/* Chat Window */}
			<div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
				<div style={{ padding: 8, borderBottom: '1px solid #eee', display: 'flex', alignItems: 'center', justifyContent: 'space-between', background: '#f7f7f7' }}>
					<span>{selectedUser ? (selectedUser.fullName || selectedUser.username) : 'Select a user'}</span>
					<button onClick={onClose} style={{ background: 'none', border: 'none', fontSize: 18, cursor: 'pointer' }}>✖</button>
				</div>
				<div style={{ flex: 1, overflowY: 'auto', padding: 12, background: '#fafbfc' }}>
					{selectedUser ? (
						messages.map((msg, idx) => (
							<div key={idx} style={{
								textAlign: msg.sender.id === user.id ? 'right' : 'left',
								margin: '8px 0',
							}}>
								<span style={{
									display: 'inline-block',
									background: msg.sender.id === user.id ? '#1976d2' : '#e0e0e0',
									color: msg.sender.id === user.id ? '#fff' : '#222',
									borderRadius: 16,
									padding: '8px 14px',
									maxWidth: 180,
									wordBreak: 'break-word',
								}}>
									{msg.content}
								</span>
							</div>
						))
					) : (
						<div style={{ color: '#888', textAlign: 'center', marginTop: 40 }}>Select a user to start chatting</div>
					)}
					<div ref={messagesEndRef} />
				</div>
				{/* Input */}
				{selectedUser && (
					<form onSubmit={sendMessage} style={{ display: 'flex', borderTop: '1px solid #eee', padding: 8, background: '#fff' }}>
						<input
							type="text"
							value={input}
							onChange={e => setInput(e.target.value)}
							placeholder="Type a message..."
							style={{ flex: 1, border: 'none', outline: 'none', fontSize: 16, padding: 8, borderRadius: 8, background: '#f5f5f5' }}
						/>
						<button type="submit" style={{ marginLeft: 8, background: isConnected ? '#1976d2' : '#aaa', color: '#fff', border: 'none', borderRadius: 8, padding: '8px 16px', fontWeight: 'bold', cursor: isConnected ? 'pointer' : 'not-allowed' }} disabled={!isConnected}>Send</button>
					</form>
						)}
						{!isConnected && (
							<div style={{ color: '#d32f2f', textAlign: 'center', fontSize: 13, marginTop: 4 }}>
								Connecting to chat server...
							</div>
						)}
			</div>
		</div>
	);
};

export default Chat;
